import React, { Component } from "react";

class Hello extends Component {
  state = {
    count: 3
  };

  render() {
    return (
      <React.Fragment>
        <span className="btn">{this.countFormat()}</span>
        <button>Increment</button>
      </React.Fragment>
    );
  }

  countFormat = () => {
    const { count } = this.state;
    return count === 0 ? <h1>ZERO</h1> : count;
  };
}

export default Hello;
