import React, { Component } from "react";

class Hello extends Component {
  state = {
    tags: [{ id: 1, count: 0 }, { id: 2, count: 0 }, {id: 3, count: 0 }]
  };

  renderTags = () => {};

  render() {
    return (
      <React.Fragment>
        {this.state.tags.map(a => (
          <div key={a.id}>
            <span className={() => { this.getBadgeClasses(a.id) }}> {() => { this.countFormat(a.id) }} </span>
            <button 
              className="btn btn-sm font-weight-bold btn-success"
              style={{ fontSize: 20 }}
            >
              {" "}
              +
            </button>
            <button

              style={{ fontSize: 20 }}className="btn btn-sm m-3 font-weight-bold
              btn-danger" >-
            </button>
          </div>
        ))}
      </React.Fragment>
    );
  }

  // handleIncrement = () => {
  //   this.setState({ count: this.state.count + 1 });
  // };

  // handleDecrement = () => {
  //   this.state.count <= 0
  //     ? this.setState({ count: 0 })
  //     : this.setState({ count: this.state.count - 1 });
  // };

  getBadgeClasses = (id) => {
    const tag = this.state.tags.find(a => id === a.id);

    const count = tag.count;
    let a = "badge m-3 badge-";
    a += count === 0 ? "warning" : "primary";
    return a;
  };

  countFormat = (id) => {
const tag = this.state.tags.find(a => id === a.id);

    const count = tag.count;
    return count === 0 ? "zero" : count;
  };
}

export default Hello;
