import React, { Component } from "react";
import Counter from "./components/hello";

class Counters extends Component {

  state = {
    tags: [
      { id: 1, count: 5 },
      { id: 2, count: 1 },
      { id: 3, count: 3 }
    ]
  };

    handleDelete = (counterId) => {
        const deleteCounter = this.state.tags.filter((count) => count.id !== counterId)
        this.setState(deleteCounter);
    }

  render() {
    return (
      <div>
        {this.state.tags.map(tag => (
          <Counter
            key={tag.id}
            value={tag.count}
            onDelete={() => { this.handleDelete(tag.id)}}
          ></Counter>
        ))}
      </div>
    );
  }

}

export default Counters;
