import React, { Component } from "react";
import Counter from "./components/hello";

class Counters extends Component {
  state = {
    tags: [
      { id: 1, count: 5 },
      { id: 2, count: 1 },
      { id: 3, count: 3 }
    ]
  };

  handleIncrement = tag => {
    const tags = [...this.state.tags];
    const index = tags.indexOf(tag);

    tags[index].count++
    this.setState({ tags: tags });
  };

  handleDelete = counterId => {
    const deleteCounter = this.state.tags.filter(
      count => count.id !== counterId
    );
    this.setState({ tags: deleteCounter });
  };

  handleReset = () => {
    const counting = this.state.tags.map(counter => {
      counter.count = 0;
      return counter;
    });
    this.setState({ count: counting });

  };

  render() {
    return (
      <div>
        <nav class="navbar navbar-light bg-light">
          <a class="navbar-brand" href="#">
            Navbar
          </a>
        </nav>
        <button
          onClick={this.handleReset}
          className="btn m-2 btn-primary btn-sm"
        >
          Reset
        </button>

        {this.state.tags.map(tag => (
          <Counter
            key={tag.id}
            tags={tag}
            onDelete={this.handleDelete}
            onIncrement={this.handleIncrement}
          ></Counter>
        ))}
      </div>
    );
  }
}

export default Counters;
