import React, { Component } from "react";
import Counter from "./components/hello";

class Counters extends Component {
  state = {
    tags: [
      { id: 1, count: 5 },
      { id: 2, count: 1 },
      { id: 3, count: 3 }
    ]
  };

  handleDelete = counterId => {
    const deleteCounter = this.state.tags.filter(
      count => count.id !== counterId
    );
    this.setState({ tags: deleteCounter });
  };

  handleReset = () => {
    // const counting = this.state.count.map(counter => {
    //   counter.count = 0;
    //   return counter;
    // });
    // this.setState({ count: counting });
    alert("yes");
  };

  render() {
    return (
      <div className="mt-3">
        <button
          onClick={this.handleResetonReset}
          className="btn m-2 btn-primary btn-sm"
        >
          Reset
        </button>

        {this.state.tags.map(tag => (
          <Counter
            key={tag.id}
            tags={tag}
            onDelete={this.handleDelete}
          ></Counter>
        ))}
      </div>
    );
  }
}

export default Counters;
