import React, { Component } from "react";

class Hello extends Component {
  state = {
    count: 0,
    tags: [
      { id: 1, count: 0 },
      { id: 2, count: 1 },
      { id: 3, count: 0 }
    ]
  };


  render() {
    return (
      <React.StrictMode>
        <React.Fragment>
          {this.state.tags.map(a => (
            <div key={a.id}>
              <span className={this.getBadgeClasses(a.id)}>
                {this.countFormat}
              </span>
              <button
                className="btn btn-sm font-weight-bold btn-success"
                style={{ fontSize: 20 }}
                onClick={this.handleIncrement()}
              >
                +
              </button>
              <button
                style={{ fontSize: 20 }}
                className="btn btn-sm m-3 font-weight-bold
              btn-danger"
              >
                -
              </button>
            </div>
          ))}
        </React.Fragment>
      </React.StrictMode>
    );
  }

  handleIncrement = id => {

    const count = this.state.count;

    this.setState({count: count + 1 });
  };

  // handleDecrement = () => {
  //   this.state.count <= 0
  //     ? this.setState({ count: 0 })
  //     : this.setState({ count: this.state.count - 1 });
  // };

  getBadgeClasses = id => {

    let a = "badge m-3 badge-";
    a += this.state.count === 0 ? "warning" : "primary";
    return a;
  };

  countFormat = () => {
    const {count
  } = this.state.count;
    return count === 0 ? "zero" : count;
  };
}

export default Hello;
