import React, { Component } from "react";

class Hello extends Component {
  render() {
    return (
      <React.Fragment>
        <div>
          <span className={this.getBadgeClasses()}>{this.countFormat()}</span>

          <button
            className="btn btn-sm mr-2 font-weight-bold btn-secondary"
            style={{ fontSize: 15 }}
            onClick={() => this.props.onIncrement(this.props.tags)}
          >
            +
          </button>
          <button
            style={{ fontSize: 15 }}
            className="btn btn-sm m-3 font-weight-bold
                btn-light"
            onClick={() => {this.props.onDecrement(this.props.tags.id)}}
          >
            -
          </button>
          <button
            onClick={() => {
              this.props.onDelete(this.props.tags.id);
            }}
            className="btn btn-danger btn-sm"
          >
            x
          </button>
        </div>
      </React.Fragment>
    );
  }

  getBadgeClasses = () => {
    let a = "badge p-3 badge-";
    a += this.props.tags.count === 0 ? "warning" : "primary";
    return a;
  };

  countFormat = () => {
    const { count } = this.props.tags;
    return count === 0 ? "zero" : count;
  };
}

export default Hello;
