import React, { Component } from "react";

class Hello extends Component {
  state = {
    tags: [{}, "tag2", "tag3"]
  };

  render() {
    return (
      <React.Fragment>
        <span className={this.getBadgeClasses()}>{this.countFormat()}</span>
        <button
          onClick={() => {
            this.handleIncrement();
          }}
          className="btn btn-sm font-weight-bold btn-success"
          style={{ fontSize: 20 }}
        >
          +
        </button>
        <button
          onClick={() => {
            this.handleDecrement();
          }}
          style={{ fontSize: 20 }}
          className="btn btn-sm m-3 font-weight-bold btn-danger"
        >
          -
        </button>
      </React.Fragment>
    );
  }

  handleIncrement = () => {
    this.setState({ count: this.state.count + 1 });
  };

  handleDecrement = () => {
    this.state.count <= 0 ? this.setState({count:  0}) : this.setState({count: this.state.count -1 })
  }

  getBadgeClasses = () => {
    const { count } = this.state;
    let a = "badge m-3 badge-";
    a += count === 0 ? "warning" : "primary";
    return a;
  };

  countFormat = () => {
    const { count } = this.state;
    return count === 0 ? "zero" : count;
  };
}

export default Hello;
