import React, { Component } from "react";

class Hello extends Component {
  render() {
    return (
      <React.Fragment>
        <div>
          <span className={this.getBadgeClasses()}>{this.countFormat()}</span>

          <button
            className="btn btn-sm font-weight-bold btn-success"
            style={{ fontSize: 15 }}
            onClick={this.props.onIncrement(this.props.counter)}
          >
            Increment
          </button>
          <button
            style={{ fontSize: 15 }}
            className="btn btn-sm m-3 font-weight-bold
                btn-danger"
            onClick={this.handleDecrement}
          >
            Decrement
          </button>
          <button
            onClick={() => {
              this.props.onDelete(this.props.tags.id);
            }}
            className="btn btn-secondary btn-sm"
          >
            Delete
          </button>
        </div>
      </React.Fragment>
    );
  }

  // handleDecrement = () => {
  //   this.props.count <= 0
  //     ? this.setState({ count: 0 })
  //     : this.setState({ count: this.props.count - 1 });
  // };

  getBadgeClasses = id => {
    let a = "badge m-3 badge-";
    a += this.props.count === 0 ? "warning" : "primary";
    return a;
  };

  countFormat = () => {
    const { count } = this.props;
    return count === 0 ? "zero" : count;
  };
}

export default Hello;
