import React, { Component } from "react";

class Hello extends Component {
  state = {
    count: this.props,

  };


  render() {
    return (
      <React.Fragment>
        <div>
          <span className={this.getBadgeClasses()}>{this.countFormat()}</span>

          <button
            className="btn btn-sm font-weight-bold btn-success"
            style={{ fontSize: 20 }}
            onClick={this.handleIncrement}
          >
            Increment
          </button>
          <button
            style={{ fontSize: 20 }}
            className="btn btn-sm m-3 font-weight-bold
                btn-danger"
            onClick={this.handleDecrement}
          >
            Decrement
          </button>
        </div>
      </React.Fragment>
    );
  }

  handleIncrement = id => {

    const count = this.state.count;

    this.setState({count: count + 1 });
  };

  handleDecrement = () => {
    this.state.count <= 0
      ? this.setState({ count: 0 })
      : this.setState({ count: this.state.count - 1 });
  };

  getBadgeClasses = id => {

    let a = "badge m-3 badge-";
    a += this.state.count === 0 ? "warning" : "primary";
    return a;
  };

  countFormat = () => {
    const {count} = this.state;
    return count === 0 ? "zero" : count;
  };
}

export default Hello;
