import React, { Component } from "react";

class Hello extends Component {
  state = {
    count: this.props.tags.count
  };

  render() {
    return (
      <React.Fragment>
        <div>
          <span className={this.getBadgeClasses()}>{this.countFormat()}</span>

          <button
            className="btn btn-sm font-weight-bold btn-success"
            style={{ fontSize: 15 }}
            onClick={this.handleIncrement}
          >
            Increment
          </button>
          <button
            style={{ fontSize: 15 }}
            className="btn btn-sm m-3 font-weight-bold
                btn-danger"
            onClick={this.handleDecrement}
          >
            Decrement
          </button>
          <button
            onClick={() => {
              this.props.onDelete(this.props.tags.id);
            }}
            onReset 
            className="btn btn-secondary btn-sm"
          >
            Delete
          </button>
        </div>
      </React.Fragment>
    );
  }

  handleReset = () => {
    const counting = this.state.count.map(counter => {
      counter.count = 0;
      return counter;
    });
    this.setState({ count: counting });
  };

  handleIncrement = id => {
    const count = this.state.count;

    this.setState({ count: count + 1 });
  };

  handleDecrement = () => {
    this.state.count <= 0
      ? this.setState({ count: 0 })
      : this.setState({ count: this.state.count - 1 });
  };

  getBadgeClasses = id => {
    let a = "badge m-3 badge-";
    a += this.state.count === 0 ? "warning" : "primary";
    return a;
  };

  countFormat = () => {
    const { count } = this.state;
    return count === 0 ? "zero" : count;
  };
}

export default Hello;
